<script type=text/javascript>
$(document).ready(function () {
  //Crea un input por cada elemento q exista en este caso en el footer de la tabla.
  $('#datatable tfoot th').each( function () {
        var title = $(this).text();
        $(this).html( '<input type="text" placeholder="Buscar por '+title+'" />' );
    } );  

  
  //DataTables
    var table = $('#datatable').DataTable({
      initComplete: function () 
      {
            // Apply the search
            this.api().columns().every( function () 
            {
                var that = this;
 
                $( 'input', this.footer() ).on( 'keyup change clear', function () 
                {
                    if ( that.search() !== this.value ) 
                    {
                        that
                            .search( this.value )
                            .draw();
                    }
                });
            });
      }
                    ,
          "dom": 'B<f<t><"#df"<"pull-left" i><"pull-right"p><"pull-right"l>>>',
         "lengthChange": true,
         "lengthMenu": [[10, 50, 100, -1], [10, 50, 100, "Todos"]],
        searching: true,
        language: {
                url: "/vendor/jQueryDT/Spanish.json"
            },
        order : [[8,"asc"]]
            ,
        "buttons": [
				{
					text: 'Exportar a PDF',
					extend: 'pdfHtml5',
					orientation: 'portrait', 
					pageSize: 'A4', //A3 , A5 , A6 , legal , letter
					download: 'open',
					customize: function (doc) {
						//Remove the title created by datatTables
						doc.content.splice(0,1);
						//Create a date string that we use in the footer. Format is dd-mm-yyyy
						var now = new Date();
            var jsDate = now.getDate()+'-'+(now.getMonth()+1)+'-'+now.getFullYear();
            var cantElem = table.data().count();
            // Logo converted to base64
            var logo = "data:image/png;base64,";
						// Set page margins [left,top,right,bottom] or [horizontal,vertical]
						// or one number for equal spread
						// It's important to create enough space at the top for a header !!!
						doc.pageMargins = [60,60,60,30];
						// Set the font size fot the entire document
						doc.defaultStyle.fontSize = 7;
						// Set the fontsize for the table header
						doc.styles.tableHeader.fontSize = 7;
						// Create a header object with 3 columns
						// Left side: Logo
						// Middle: brandname
						// Right side: A document title
						doc['header']=(function() {
							return {
								columns: [
									{
										image: logo,
										width: 80
									},
									{
										alignment: 'center',
										text: 'REPORTE DE LAS SOLICITUDES INSCRITAS',
										fontSize: 10,
										margin: [0,0,100,0]
									}
									
								],
								margin: 20
							}
						});
						// Create a footer object with 2 columns
						// Left side: report creation date
						// Right side: current page and total pages
						doc['footer']=(function(page, pages) {
							return {
								columns: [
									{
										alignment: 'left',
										text: ['Creado el: ', { text: jsDate.toString() }]
                  },
                  {
                    alignment: 'center',
										text: ['Cantidad Traductores: ', { text: cantElem.toString() }]
                  },
									{
										alignment: 'right',
										text: ['p谩gina ', { text: page.toString() },	' de ',	{ text: pages.toString() }]
                  }
                  
								],
								margin: 20
							}
						});
						// Change dataTable layout (Table styling)
						// To use predefined layouts uncomment the line below and comment the custom lines below
						// doc.content[0].layout = 'lightHorizontalLines'; // noBorders , headerLineOnly
						var objLayout = {};
						objLayout['hLineWidth'] = function(i) { return .5; };
						objLayout['vLineWidth'] = function(i) { return .5; };
						objLayout['hLineColor'] = function(i) { return '#aaa'; };
						objLayout['vLineColor'] = function(i) { return '#aaa'; };
						objLayout['paddingLeft'] = function(i) { return 4; };
						objLayout['paddingRight'] = function(i) { return 4; };
						doc.content[0].layout = objLayout;
				}
        },
        {
                extend: 'print',
                text: 'Imprimir',
                orientation:'portrait'
            }
      ]
});
